svg.script:
const rect = document.querySelector("#rect");
let angle = 0;

// animation
function animate(time) {
    angle += 1;

    if(angle >= 360) {
        angle = 0;
    }

    rect.setAttribute("transform", `rotate(${angle} 100 100)`);
    requestAnimationFrame(animate);
}

requestAnimationFrame(animate);

// test external scripts
const element = document.createElementNS('http://www.w3.org/2000/svg', 'text');

element.setAttribute('x', 20);
element.setAttribute('y', 20);

try {
    $('g').append(element);
    element.innerHTML = 'jQuery loaded';
} catch {
    document.querySelector('g').appendChild(element);
    element.innerHTML = 'jQuery not loaded';
}
